import styled from "styled-components";

type ContainerProps = {
  showBackGround: boolean;
}
export const Container = styled.div<ContainerProps>`
  background-color: ${props=> props.showBackGround ? '#1550FF' : '#E2E#E#'};
`;

export const Icon = styled.img`
`;
